mapper.select:
SELECT photo_url,link_address,link_type,product_spu_sn,banner_name
FROM mall_product.mall_banner
WHERE del_flag = 0
AND   location = #{location}
AND   show_status = #{showStatus}
ORDER BY sort

SELECT count(*)
FROM mall_product.mall_banner
where sort >= #{sort}

mapper.update:
UPDATE mall_product.mall_banner
SET
show_status = #{showStatus}
WHERE
id IN

    #{item}

mapper.select:
SELECT mall_banner.id,banner_name,photo_url,link_address,location,link_type,product_spu_sn,mall_banner.sort,start_time,end_time,show_status,master_image_url FROM mall_banner LEFT JOIN pms_product_basics_info ON spu_code = product_spu_sn WHERE mall_banner.del_flag = 0
AND banner_name LIKE CONCAT('%',#{bannerName},'%')
AND show_status = #{showStatus}
